	 /**
     * @description TODO Base64图片上传
     * @param request
     * @param path
     * @return
     * @date 2017年12月25日下午12:02:10
     * @author geYang
     * @throws MyException 
     **/
    public static String uplodeBase64Image(HttpServletRequest request,String path,String imgData) throws MyException{
        if(imgData==null){
            System.out.println("Base64图片上传为空");
        } else {
            //文件存入地址
            String realPath = request.getSession().getServletContext().getRealPath("/");
            //随机生成图片名字
            String uuid = "BASE64-"+UUID.randomUUID()+".jpg";
            //返回的数据库地址
            String url = path+uuid;
            String urlpath = realPath+path;
            System.out.println("BASE64上传文件地址:"+urlpath);
            System.out.println("BASE64上传返回地址:"+url);
            byte[] bs = Base64Utils.decodeFromString(imgData);
            try {
                //判断该路径文件夹是否存在
                File files = new File(urlpath);
                if(!files.exists()||!files.isDirectory()){
                    //创建文件夹
                    files.mkdirs();
                }
                out = new FileOutputStream(urlpath+uuid);
                out.write(bs);
                return url;
            } catch (IOException e) {
                e.printStackTrace();
                throw new MyException("BASE64图片上传错误");
            }
        }
        return null;
    }
    
    
    
